css:
/* 玻璃态主题样式 - Glassmorphism Theme */

/* 重置和基础样式 */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'SF Pro Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: linear-gradient(135deg, 
        #667eea 0%, 
        #764ba2 25%, 
        #f093fb 50%, 
        #f5576c 75%, 
        #4facfe 100%);
    min-height: 100vh;
    position: relative;
    overflow-x: hidden;
}

/* 背景装饰形状 */
.glass-bg {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: -1;
}

.glass-shape {
    position: absolute;
    border-radius: 50%;
    background: linear-gradient(45deg, 
        rgba(255, 255, 255, 0.1),
        rgba(255, 255, 255, 0.05));
    backdrop-filter: blur(10px);
    animation: float 20s infinite ease-in-out;
}

.glass-shape-1 {
    width: 300px;
    height: 300px;
    top: 10%;
    left: 10%;
    animation-delay: 0s;
}

.glass-shape-2 {
    width: 200px;
    height: 200px;
    top: 60%;
    right: 15%;
    animation-delay: -7s;
}

.glass-shape-3 {
    width: 150px;
    height: 150px;
    bottom: 20%;
    left: 60%;
    animation-delay: -14s;
}

@keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    25% { transform: translateY(-20px) rotate(90deg); }
    50% { transform: translateY(-40px) rotate(180deg); }
    75% { transform: translateY(-20px) rotate(270deg); }
}

/* 主容器 */
.glass-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    position: relative;
    z-index: 1;
}

/* 玻璃态导航栏 */
.glass-nav {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 20px;
    padding: 15px 30px;
    margin-bottom: 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-shadow: 
        0 8px 32px rgba(0, 0, 0, 0.1),
        inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

.glass-nav-brand h2 {
    color: rgba(255, 255, 255, 0.95);
    font-weight: 600;
    font-size: 24px;
}

.glass-nav-items {
    display: flex;
    gap: 25px;
}

.glass-nav-item {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    padding: 10px 20px;
    border-radius: 12px;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.glass-nav-item:hover {
    color: rgba(255, 255, 255, 1);
    background: rgba(255, 255, 255, 0.1);
    transform: translateY(-2px);
}

.glass-nav-item::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.5s;
}

.glass-nav-item:hover::before {
    left: 100%;
}

/* 内容区域 */
.glass-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 25px;
}

/* 玻璃态卡片 */
.glass-card {
    background: rgba(255, 255, 255, 0.12);
    backdrop-filter: blur(25px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 24px;
    padding: 25px;
    box-shadow: 
        0 12px 40px rgba(0, 0, 0, 0.15),
        inset 0 1px 0 rgba(255, 255, 255, 0.2);
    transition: all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
    position: relative;
    overflow: hidden;
}

.glass-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, 
        transparent, 
        rgba(255, 255, 255, 0.3), 
        transparent);
}

.glass-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 
        0 20px 60px rgba(0, 0, 0, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.3);
}

/* 卡片变体 */
.glass-card-primary {
    background: rgba(102, 126, 234, 0.15);
    border-color: rgba(102, 126, 234, 0.2);
}

.glass-card-secondary {
    background: rgba(118, 75, 162, 0.15);
    border-color: rgba(118, 75, 162, 0.2);
}

.glass-card-accent {
    background: rgba(240, 147, 251, 0.15);
    border-color: rgba(240, 147, 251, 0.2);
}

/* 卡片头部 */
.glass-card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}

.glass-card-header h3 {
    color: rgba(255, 255, 255, 0.95);
    font-size: 20px;
    font-weight: 600;
}

/* 徽章 */
.glass-badge {
    background: linear-gradient(45deg, #ff6b6b, #feca57);
    color: white;
    padding: 4px 12px;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    box-shadow: 0 4px 15px rgba(255, 107, 107, 0.3);
}

/* 卡片内容 */
.glass-card-body {
    color: rgba(255, 255, 255, 0.85);
    line-height: 1.6;
}

/* 统计数据 */
.glass-stats {
    display: flex;
    justify-content: space-around;
    gap: 15px;
}

.glass-stat-item {
    text-align: center;
    padding: 15px;
    background: rgba(255, 255, 255, 0.08);
    border-radius: 16px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    flex: 1;
    transition: all 0.3s ease;
}

.glass-stat-item:hover {
    background: rgba(255, 255, 255, 0.12);
    transform: translateY(-3px);
}

.glass-stat-number {
    display: block;
    font-size: 28px;
    font-weight: 700;
    color: rgba(255, 255, 255, 0.95);
    margin-bottom: 5px;
}

.glass-stat-label {
    font-size: 14px;
    color: rgba(255, 255, 255, 0.7);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* 玻璃态按钮 */
.glass-btn {
    background: rgba(255, 255, 255, 0.15);
    border: 1px solid rgba(255, 255, 255, 0.2);
    color: rgba(255, 255, 255, 0.95);
    padding: 12px 24px;
    border-radius: 16px;
    cursor: pointer;
    font-weight: 500;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    position: relative;
    overflow: hidden;
}

.glass-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, 
        transparent, 
        rgba(255, 255, 255, 0.1), 
        transparent);
    transition: left 0.5s;
}

.glass-btn:hover::before {
    left: 100%;
}

.glass-btn:hover {
    background: rgba(255, 255, 255, 0.25);
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

.glass-btn-primary {
    background: linear-gradient(45deg, rgba(102, 126, 234, 0.3), rgba(79, 172, 254, 0.3));
    border-color: rgba(102, 126, 234, 0.4);
}

.glass-btn-accent {
    background: linear-gradient(45deg, rgba(240, 147, 251, 0.3), rgba(245, 87, 108, 0.3));
    border-color: rgba(240, 147, 251, 0.4);
}

/* 玻璃态表单 */
.glass-form {
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.glass-form-group {
    position: relative;
}

.glass-input,
.glass-select {
    width: 100%;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 12px;
    padding: 12px 16px;
    color: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
}

.glass-input::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.glass-input:focus,
.glass-select:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.4);
    background: rgba(255, 255, 255, 0.15);
    box-shadow: 0 0 20px rgba(255, 255, 255, 0.1);
}

/* 响应式设计 */
@media (max-width: 768px) {
    .glass-container {
        padding: 15px;
    }

    .glass-nav {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }

    .glass-nav-items {
        justify-content: center;
    }

    .glass-content {
        grid-template-columns: 1fr;
    }

    .glass-stats {
        flex-direction: column;
    }
}

/* 额外的动画效果 */
@keyframes glassShimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
}

.glass-card:hover::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        90deg,
        transparent,
        rgba(255, 255, 255, 0.05),
        transparent
    );
    background-size: 200% 100%;
    animation: glassShimmer 2s ease-in-out;
    pointer-events: none;
}
