css:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background-color: #fafbfc;
    color: #334155;
    line-height: 1.6;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 24px;
}

/* Header */
.header {
    background-color: #ffffff;
    border-bottom: 1px solid #e2e8f0;
    padding: 16px 0;
    position: sticky;
    top: 0;
    z-index: 100;
}

.header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-brand h1 {
    font-size: 20px;
    font-weight: 600;
    color: #1e293b;
}

.nav-actions {
    display: flex;
    align-items: center;
    gap: 16px;
}

.theme-btn {
    padding: 8px 16px;
    background-color: #f1f5f9;
    border: 1px solid #e2e8f0;
    border-radius: 8px;
    font-size: 14px;
    font-weight: 500;
    color: #475569;
    cursor: pointer;
    transition: all 0.2s ease;
}

.theme-btn:hover {
    background-color: #e2e8f0;
    border-color: #cbd5e1;
}

.user-profile {
    padding: 8px 12px;
    background-color: #f8fafc;
    border-radius: 8px;
    font-size: 14px;
    font-weight: 500;
    color: #64748b;
}

/* Main Content */
.main-content {
    padding: 32px 0;
}

.section-title {
    font-size: 24px;
    font-weight: 600;
    color: #1e293b;
    margin-bottom: 24px;
}

/* Stats Section */
.stats-section {
    margin-bottom: 48px;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 24px;
}

.stat-card {
    background-color: #ffffff;
    border: 1px solid #f1f5f9;
    border-radius: 12px;
    padding: 24px;
    display: flex;
    align-items: center;
    gap: 16px;
    transition: all 0.2s ease;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.05);
}

.stat-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.08);
    border-color: #e2e8f0;
}

.stat-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 48px;
    height: 48px;
    background-color: #f8fafc;
    border-radius: 10px;
    color: #64748b;
    flex-shrink: 0;
}

.stat-content {
    flex: 1;
}

.stat-value {
    font-size: 28px;
    font-weight: 700;
    color: #1e293b;
    line-height: 1.2;
    margin-bottom: 4px;
}

.stat-label {
    font-size: 14px;
    font-weight: 500;
    color: #64748b;
    margin-bottom: 8px;
}

.stat-trend {
    font-size: 12px;
    font-weight: 600;
    padding: 2px 8px;
    border-radius: 12px;
    display: inline-block;
}

.stat-trend.positive {
    background-color: #f0fdf4;
    color: #16a34a;
}

.stat-trend.stable {
    background-color: #f8fafc;
    color: #64748b;
}

/* Actions Section */
.actions-section {
    margin-bottom: 48px;
}

.actions-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 20px;
}

.action-card {
    background-color: #ffffff;
    border: 1px solid #f1f5f9;
    border-radius: 12px;
    padding: 24px;
    display: flex;
    align-items: flex-start;
    gap: 16px;
    transition: all 0.2s ease;
    cursor: pointer;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.05);
}

.action-card:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.08);
    border-color: #e2e8f0;
}

.action-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    background-color: #f8fafc;
    border-radius: 8px;
    color: #64748b;
    flex-shrink: 0;
}

.action-content h3 {
    font-size: 16px;
    font-weight: 600;
    color: #1e293b;
    margin-bottom: 4px;
}

.action-content p {
    font-size: 14px;
    color: #64748b;
    line-height: 1.5;
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 0 16px;
    }

    .main-content {
        padding: 24px 0;
    }

    .section-title {
        font-size: 20px;
        margin-bottom: 20px;
    }

    .stats-grid,
    .actions-grid {
        grid-template-columns: 1fr;
        gap: 16px;
    }

    .stat-card,
    .action-card {
        padding: 20px;
    }

    .nav-actions {
        gap: 12px;
    }

    .theme-btn {
        padding: 6px 12px;
        font-size: 13px;
    }
}

/* Focus and Accessibility */
.theme-btn:focus,
.action-card:focus {
    outline: 2px solid #3b82f6;
    outline-offset: 2px;
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}
